import "./App.scss";
import { Client, Message } from "paho-mqtt";
import { useState, useEffect } from "react";
function App() {
  let client = {};
  const [relayData, setrelayData] = useState([
    {
      name: "RELAY 1",
      state: true,
    },
    {
      name: "RELAY 2",
      state: true,
    },
    {
      name: "RELAY 3",
      state: false,
    },
    {
      name: "RELAY 4",
      state: false,
    },
  ]);
  const options = {
    host: "42c1410bece7497a99cfa15427041510.s2.eu.hivemq.cloud",
    port: 8884,
    protocol: "mqtts",
    username: "thaianhtaivn",
    password: "159734628",
    clientId: `my-client-id-${Math.random()}`,
  };
  useEffect(() => {
    client = new Client(options.host, options.port, options.clientId);
    function connect() {
      // set callback handlers
      client.onConnectionLost = onConnectionLost;
      client.onMessageArrived = onMessageArrived;

      // set options
      const connectOptions = {
        onSuccess: onConnect,
        useSSL: true,
        userName: options.username,
        password: options.password,
      };
      client.connect(connectOptions);
    }

    function onConnect() {
      console.log("Connected to HiveMQ broker");
      client.subscribe("Hello");
    }

    function onConnectionLost(responseObject) {
      if (responseObject.errorCode !== 0) {
        console.log("Connection lost:", responseObject.errorMessage);
      }
    }

    function onMessageArrived(message) {
      console.log("Received message:", message.payloadString);
    }

    connect();
    return () => {
      client.disconnect();
    };
  }, []);

  function handleSwitch(value) {
    console.log(value);
    const message = new Message(JSON.stringify(value));
    message.destinationName = "Relay-4-";
    message.qos = 0;
    client.send(message);
  }

  return (
    <div className="App">
      <div className="app-header">
        <h3>RELAY CONTROLLER APP</h3>
      </div>
      <div className="switch-btns">
        <div className="relay-id d-flex">
          <label class="col-form-label me-2">Relay ID:</label>
          <div class="col">
            <input type="email" class="form-control" placeholder="Relay ID" />
          </div>
        </div>
        {Object.values(relayData).map((item, index) => (
          <div className="form-check form-switch" key={index}>
            <label className="">{item.name}</label>
            <input className="form-check-input" type="checkbox" defaultChecked={item.state} onChange={() => handleSwitch(item)} />
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
