import "./App.scss";
import { Client } from "paho-mqtt";
import { useState, useEffect } from "react";
function App() {
  const [relayData, setrelayData] = useState([
    {
      name: "RELAY 1",
      state: true,
    },
    {
      name: "RELAY 2",
      state: true,
    },
    {
      name: "RELAY 3",
      state: false,
    },
    {
      name: "RELAY 4",
      state: false,
    },
  ]);
  // const client = new Client("mqtts://42c1410bece7497a99cfa15427041510.s2.eu.hivemq.cloud:8333", "client-id");
  const options = {
    host: "42c1410bece7497a99cfa15427041510.s2.eu.hivemq.cloud",
    port: 8883,
    protocol: "mqtts",
    username: "thaianhtaivn",
    password: "<your_password>",
    clientId: "my-client-id",
  };
  const client = new Client(options.host, options.port, options.clientId);
  return (
    <div className="App">
      <div className="app-header">
        <h3>RELAY CONTROLLER APP</h3>
      </div>
      <div className="switch-btns">
        {Object.values(relayData).map((item, index) => (
          <div className="form-check form-switch" key={index}>
            <label className="">{item.name}</label>
            <input className="form-check-input" type="checkbox" id="switch-1" defaultChecked={item.state} />
          </div>
        ))}
      </div>
      <button className="btn btn-primary">Hello</button>
    </div>
  );
}

export default App;
